import React, { useEffect, useRef, useState } from 'react';
import { GitHub, MenuRounded } from '@mui/icons-material';
import { Footer } from './section/footer';
import { Landing } from './section/landing.js';

function App() {
  const [lang, setLang] = useState('en'); // Menyimpan bahasa
  const sections = useRef({
    home: null,
    project: null,
    skill: null,
    about: null,
  }); // Referensi untuk setiap section
  const isMobile = window.innerWidth < 768; // Deteksi perangkat
  const [currentPage, setCurrentPage] = useState('home'); // Halaman aktif

  const switchLang = () => setLang(lang === 'id' ? 'en' : 'id'); // Ganti bahasa

  const scrollToSection = (section) => {
    sections.current[section]?.scrollIntoView({ behavior: 'smooth' }); // Scroll ke bagian
  };

  useEffect(() => {
    const observerOptions = { root: null, threshold: 0.5 }; // Opsi IntersectionObserver
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          // Jika section terlihat lebih dari 50%
          const section = Object.keys(sections.current).find(
            (key) => sections.current[key] === entry.target
          );
          setCurrentPage(section); // Setel halaman aktif
        }
      });
    }, observerOptions);

    // Observasi setiap section
    Object.values(sections.current).forEach((section) =>
      observer.observe(section)
    );

    return () =>
      // Hentikan observasi saat komponen dilepas
      Object.values(sections.current).forEach((section) =>
        observer.unobserve(section)
      );
  }, []);

  return (
    <div className="App" style={{ backgroundColor: '#4682B4' }}>
      {/* Header */}
      <header className="sticky top-0 grid lg:grid-cols-2 z-50 border-b border-blue-300/20 items-center backdrop-blur-md">
        <div className="text-start lg:text-start p-4 uppercase tracking-wide h-fit text-md font-medium lg:font-semibold text-yellow-200">
          {/* Desktop menu */}
          <div className="hidden lg:block">
            <span
              onClick={() => scrollToSection('home')}
              className={`lg:mx-1 p-2 hover:text-yellow-200 cursor-pointer ${currentPage === 'home' && 'text-black'
                }`}
            >
              {lang === 'id' ? 'Beranda' : 'Home'}
            </span>
            <span
              onClick={() => scrollToSection('project')}
              className={`lg:mx-1 p-2 hover:text-yellow-200 cursor-pointer ${currentPage === 'project' && 'text-black'
                }`}
            >
              {lang === 'id' ? 'Projek' : 'Projects'}
            </span>
            <span
              onClick={() => scrollToSection('skill')}
              className={`lg:mx-1 p-2 hover:text-yellow-200 cursor-pointer ${currentPage === 'skill' && 'text-black'
                }`}
            >
              {lang === 'id' ? 'Keahlian' : 'Skills'}
            </span>
            <span
              onClick={() => scrollToSection('about')}
              className={`lg:mx-1 p-2 hover:text-yellow-200 cursor-pointer ${currentPage === 'about' && 'text-black'
                }`}
            >
              {lang === 'id' ? 'Tentang Saya' : 'About Me'}
            </span>
          </div>

          {/* Mobile menu */}
          <div className="block lg:hidden">
            <div className="flex justify-between">
              <MenuRounded />
              <p className="text-end uppercase" onClick={switchLang}>
                {lang}
              </p>
            </div>
          </div>
        </div>

        <div className="text-end p-4 pe-8 hidden lg:block">
          <span
            className="uppercase bg-yellow-200 align-middle me-4 opacity-75 hover:opacity-100 p-1.5 rounded font-semibold text-black"
            onClick={switchLang}
          >
            {lang}
          </span>
          <a href="https://github.com/JukiSufail" className="text-black">
            <GitHub className="bg-yellow-200 p-1 rounded scale-150 opacity-75 hover:opacity-100" />
          </a>
        </div>
      </header>

      {/* Main Content */}
      <div>
        <div ref={(el) => (sections.current.home = el)}>
          <Landing lang={lang} />
        </div>
      </div>

      {/* Footer */}
      <Footer lang={lang} />
    </div>
  );
}

export default App;
